import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import { Button, Backdrop, AppBar, Box, Container } from '@material-ui/core';
import { withStyles, responsiveFontSizes } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 2 ,
    color: 'black',
    fontSize: '25px',
    width: '80%',
    maxWidth: '60%',
    direction: 'column',
    justify: "space-around",
    alignItems: "center"
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.default,
  },
}));

export default function AutoGrid() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
     <h2 style={{fontSize: '45px', textAlign: 'center'}}>Shop</h2>
      <Grid container spacing={3}>
        <Grid item xs>
        <img src={"/static/teeshirt.jpeg"} width='350px' maxwidth='500px' height='auto'/>
        </Grid>
        <Grid item xs={6}>
            <Button style={{ borderWidth: 2, borderColor: 'black', borderTopLeftRadius: 1, borderStyle:'solid' }}>Select Size</Button>
        </Grid>
        <Grid item xs>
        <p style={{width: '80%'}}>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit,
            sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. 
            Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. 
            Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. 
            Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt 
            mollit anim id est laborum.
        </p>
        </Grid>
      </Grid>
      <Grid container spacing={3}>
        <Grid item xs>
        <p style={{width: '80%'}}>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit,
            sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. 
            Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. 
            Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. 
            Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt
        </p>
        </Grid>
        <Grid item xs={6}>
            <Button style={{ borderWidth: 2, borderColor: 'black', borderTopLeftRadius: 1, borderStyle:'solid' }}>Select Size</Button>
        </Grid>
      </Grid>
    </div>
  );
}
