import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import ButtonAppBar from '../components/buttonAppBar';
import { withStyles, responsiveFontSizes } from '@material-ui/core/styles';
import { Button, Backdrop, AppBar, Box, Container } from '@material-ui/core';
import SimpleCard from '../components/userInfo'
import Feed from '../components/feed'
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';
import FavoriteIcon from '@material-ui/icons/Favorite';
import Typography from '@material-ui/core/Typography';




const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    maxWidth: '80%'
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
}));

const StyledBoxBlue = withStyles({
    root: {
      background: '#a4d8ef',
      width: '100%',
      minWidth: '100%',
      height: '600px',
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      marginLeft: '0px',
      color: "white",
      fontSize: "60px",
      fontFamily: "Arial, Helvetica, sans-serif"
    },
  })(Box);

  const StyledBoxWhite = withStyles({
    root: {
      background: '#FFFFFF',
      width: '100%',
      minWidth:'100%',
      height: '100%',
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      marginLeft: '0px',
      color: "white",
      fontSize: "60px",
      fontFamily: "Arial, Helvetica, sans-serif"
    },
  })(Box);

export default function CenteredGrid() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Grid container spacing={3}>
        <Grid item xs={8}>
          <SimpleCard />
        </Grid>
        <Grid item xs={2}>
          <Card style={{ textAlign: 'center' }}>
            <MonetizationOnIcon fontSize="large"style={{ marginTop: '30px' }}/>
            <Typography variant="h3" component="h2">
                500$
            </Typography>
            <p style={{ fontSize: '20px' }}>Money Raised</p>
          </Card>
        </Grid>
        <Grid item xs={2}>
          <Card style={{ textAlign: 'center' }}>
            <FavoriteIcon fontSize="large"/>
            <Typography variant="h3" component="h2">
                50$
            </Typography>
            <p style={{ fontSize: '20px' }}>Money Donated</p>
          </Card>
        </Grid>
        <Grid item xs={8}>
          <Feed/>
        </Grid>
        <Grid item xs={4}>
          <Card style={{ textAlign: 'center' }}>
            <Typography variant="h4" component="h2" style={{ minHeight: '500px', borderBottom: "2px solid rgb(212, 212, 212)", marginTop: '30px'}}>
                Recent Donations
            </Typography>
          </Card>
        </Grid>
      </Grid>
    </div>
  );
}
