import React from 'react';
import { Button, Backdrop, AppBar, Box, Container } from '@material-ui/core';
import ButtonAppBar from '../components/buttonAppBar';
import GridTest from '../components/gridTest'
import { withStyles, responsiveFontSizes } from '@material-ui/core/styles';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch
} from "react-router-dom";
import Typography from '@material-ui/core/Typography';
const THREE = require('three');

const WrapperBox = withStyles({
  root: {
    background: '#a4d8ef',
    width: '100%',
    height: '100%',
    display: "table",
    zIndex: "unset"
  },
})(Box);

const StyledBoxBlue = withStyles({
  root: {
    background: '#a4d8ef',
    width: '100%',
    minWidth: '100%',
    height: '600px',
    display: "block",
    textAlign: 'center',
    position: 'relative',
    justifyContent: "center",
    alignItems: "center",
    marginLeft: '0px',
    color: "white",
    fontSize: "60px",
    fontFamily: "Arial, Helvetica, sans-serif"
  },
})(Box);

const StyledBoxWhite = withStyles({
  root: {
    background: '#FFFFFF',
    width: '100%',
    minWidth:'100%',
    height: '100%',
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    marginLeft: '0px',
    color: "white",
    fontSize: "60px",
    fontFamily: "Arial, Helvetica, sans-serif"
  },
})(Box);

const HomeOptions = withStyles({
  root: {
    background: '#a4d8ef',
    width: '100%',
    height: '200px',
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    marginLeft: '0px',
    color: "white",
    fontSize: "25px",
    fontFamily: "Arial, Helvetica, sans-serif",
  },
})(Box);


const StyledButton = withStyles({
  root: {
    background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    borderRadius: 3,
    border: 0,
    color: 'white',
    height: 48,
    padding: '0 30px',
    boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
  },
  label: {
    textTransform: 'capitalize',
  },
})(Button);

const cta = ["\t" + "Save", "Wearable Charity"]

const styles = {
  root: {
    flexGrow: 1,
    lineHeight: '20px'
  },
  br: {
    lineHeight: '20px'
  },
  typography: {
    flexGrow: 1,
    textAlign: "center",
    paddingBottom: '20px'
  }
};

const App = () => (

  <WrapperBox>
      <ButtonAppBar />
      <StyledBoxBlue>
        <img src='../static/Ellipse 47.png' style={{
          position: 'relative', height: '100%', maxHeight: '100%', maxWidth: 'auto', display: 'block', marginLeft: 'auto', marginRight: 'auto', zIndex: '1' }}/>
        <h2  style={{whiteSpace: "pre-line", marginTop: '-420px', zIndex: '2', position: 'relative'}}>{cta.join('\n')}</h2>
      </StyledBoxBlue>
      <HomeOptions>
        <a style={{marginRight: "150px"}}>Mission Statement</a>
        <a style={{marginLeft: "150px"}}>Click to Shop</a>
      </HomeOptions>
      <StyledBoxWhite>
          <GridTest />
      </StyledBoxWhite>
      <StyledBoxBlue style={{paddingBottom: '4em'}}>
      <div style={{ display: 'inline-block', marginTop: '250px', maxWidth: '40%', zIndex: '2', position: 'relative', textAlign: 'center', lineHeight: '30px'}}>
          <Typography variant="h3" component="h2">SAVE STATEMENT</Typography>
          <br/>
          <Typography variant="h5" component="h2">WHAT IF WE COULD USE THE FORGOTTEN MOMENTS OF OUR DAY TO HELP SAVE OUR WORLD?</Typography>
          <br/>
          <Typography variant="body2" component="p"> IF THE SECONDS WE SPENT WAITING AND SCROLLING, WE COULD USE TO SAVE LIVES.</Typography>
          <br/>
          <Typography variant="body2" component="p">WHAT IF WE COULD DO THIS BY JUST GETTING DRESSED. </Typography>
          <br/>
          <Typography variant="body2" component="p">
            SAVE ASKS THAT YOU ACKNOWLEDGE THE UNSETTLING TRUTH THAT OUR PLANET NEEDS HELP TO TAKE THE FIRST STEP. FOR IF SOMETHING IS TO BE SAVED, IT MUST FIRST BE SEEN. 
          </Typography>
        </div>
        <img src='../static/Ellipse 47.png' style={{
            position: 'relative', height: '100%', maxHeight: '100%', maxWidth: 'auto', display: 'block', marginLeft: 'auto', marginRight: 'auto', zIndex: '1',
            marginTop: '-7em'    
        }}/>

      </StyledBoxBlue>
    </WrapperBox>

)

export default withStyles(styles)(App);
